-- Table structure for sys_ac_article
-- ----------------------------
DROP TABLE IF EXISTS `sys_ac_article`;
CREATE TABLE `sys_ac_article` (
  `article_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '导航id',
  `category_id` bigint(20) DEFAULT '0' COMMENT '分类id',
  `article_title` varchar(126) DEFAULT '' COMMENT '文章标题',
  `subhead` varchar(64) DEFAULT '' COMMENT '副标题',
  `source` varchar(126) DEFAULT '' COMMENT '来源',
  `description` varchar(126) DEFAULT '' COMMENT '描述',
  `thumbnail` varchar(126) DEFAULT '' COMMENT '缩略图',
  `art_hits` int(10) DEFAULT '0' COMMENT '访问量',
  `content` text COMMENT '内容',
  `art_type` char(1) DEFAULT '0' COMMENT '类型（0正常 1推荐 2置顶）',
  `art_status` char(1) DEFAULT '0' COMMENT '状态（0正常 1停用）',
  `remark` varchar(64) DEFAULT '' COMMENT '备注',
  `update_by` varchar(30) DEFAULT '' COMMENT '更新者',
  `update_time` datetime DEFAULT NULL COMMENT '更新日期',
  `create_by` varchar(30) DEFAULT '' COMMENT '创建者',
  `create_time` datetime DEFAULT NULL COMMENT '创建日期',
  PRIMARY KEY (`article_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1770446781955571715 DEFAULT CHARSET=utf8mb4 COMMENT='文章信息表';

